import Menu from "./Components/Menu";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './Pages/Home';
import GenerateLetter from './Pages/GenerateLetter';
import AboutUs from './Pages/AboutUs';
import ContactUs from './Pages/ContactUs';
import Donate from './Pages/Donate';
import Disclaimer from './Pages/Disclaimer';
import PrivacyPolicy from './Pages/PrivacyPolicy';
import Footer from './Components/Footer';
import './App.css';
import LoadingSpinner from "./Components/Loader";
import { useState, useEffect } from "react";

function App() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
  }, []);

  const handleLoad = () => {
    setLoading(false);
  };

  return (
    <div className="App">
      {loading && <LoadingSpinner />}
      <Router>
        <Menu />
        <Routes>
          <Route path="/" element={<Home onLoad={handleLoad} />} />
          <Route path="/generate-letter" element={<GenerateLetter onLoad={handleLoad} />} />
          <Route path="/about-us" element={<AboutUs onLoad={handleLoad} />} />
          <Route path="/contact-us" element={<ContactUs onLoad={handleLoad} />} />
          <Route path="/donate" element={<Donate onLoad={handleLoad} />} />
          <Route path="/disclaimer" element={<Disclaimer onLoad={handleLoad} />} />
          <Route path="/privacy-policy" element={<PrivacyPolicy onLoad={handleLoad} />} />
        </Routes>
        <Footer />
      </Router>
    </div>
  );
}

export default App;
