import React, { useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import "../Styles/Home.css";
import LoadingSpinner from "../Components/Loader";
import { useState } from "react";
import backgroundImage from '../Images/Home.jpg';
function Home() {
  useEffect(() => {
    setLoading(false);
  }, []);
  const [loading, setLoading] = useState(true);

  //Show Start of page after navigation

  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  const handleImageLoad = () => {
    setLoading(false);
  };
  return (
    <div>
      {loading ? <LoadingSpinner message={'Loading....'} /> :
        <div className='home' style={{ backgroundImage: `url(${backgroundImage})` }}>
          <img src={backgroundImage} alt="Background" onLoad={handleImageLoad} style={{ display: 'none' }} />
          <h1 id='h'>Letter Generator Tool</h1>
          <p className='p'>Welcome to our website, where you can generate letters quickly and easily using our advanced GPT bot. Whether you need to write a cover letter, a recommendation letter, or any other kind of letter, our bot is here to help. Simply enter your information and let the bot do the rest!</p>
          <p className='p'>Our website is designed to provide you with a seamless experience from start to finish. We have created a user-friendly form that allows you to input all the necessary information for your letter. Once you submit the form, our bot uses natural language processing to generate a draft of your letter.</p>
          <p className='p'>In addition to our letter generator, we also provide information about our creators and how to get in touch with us. We are passionate about providing our users with the best experience possible, and we are always happy to hear from you.</p>
          <p className='p' id='last_line'><b>So why wait? Try our letter generator today and see how easy it is to create professional, high-quality letters in minutes.</b></p>
          <div className='btn-div'><NavLink to="/generate-letter"><button id='btn'>Try Now</button></NavLink></div>
        </div>
      }
    </div>
  );
}

export default Home;
