import React, { useEffect } from 'react';
import { useState } from 'react';
import "../Styles/GenerateLetter.css";
import axios from 'axios'
import LoadingSpinner from '../Components/Loader';
import emailjs from 'emailjs-com';
function GenerateLetter() {

  //Show Start of page after navigation

  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  const [isLoading, setIsLoading] = useState(false);
  const [isSuccessful, setIsSuccessful] = useState(false);
  const [isFailed, setIsFailed] = useState(false);

  //Send Mail Function

  /* function SendEmail(body) {
    const serviceId = 'service_1z8i09p';
    const templateId = 'template_wt98w4j';
    const owner_mail = 'l192723@lhr.nu.edu.pk';
    const userId = 'pcdyVOeNUOmq0OcOp';

    const templateParams = {
      from_name: name,
      to_name: "Write On Time",
      from_mail: email,
      message: body,
      to_mail: owner_mail,
    };
    setIsLoading(true);
    emailjs.send(serviceId, templateId, templateParams, userId)
      .then((response) => {
        setIsLoading(false);
        setIsSuccessful(true);
        setTimeout(() => {
          setIsSuccessful(false);
        }, 10300);
        //ClearForm();
      }, (error) => {
        setIsLoading(false)
        setIsFailed(true);
        setTimeout(() => {
          setIsFailed(false);
        }, 10300);
        return;
      });
  } */

  //All Form Fields

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    recipientName: '',
    relationship: '',
    letterPurpose: '',
    situationDescription: '',
    relationshipDuration: '',
    characterTraits: '',
    achievements: '',
    personalGrowth: '',
    contribution: '',
    desiredJob: '',
    skills: '',
    accomplishments: '',
    education: '',
    personalAttributes: '',
    willingnessToLearn: '',
    employmentExplanation: '',
    additionalInformation: '',
    tone: '',
    omittedInformation: '',
  });

  //Assigning value after every change in form

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  //Form Submit Handler

  const handleSubmit = async (event) => {
    event.preventDefault();
    let request;
    if (formData.letterPurpose === 'character') {
      request = `Write a draft of the letter for the following information of ${formData.recipientName}. sender name: ${formData.name}, sender email: ${formData.email}, full name of the person the letter is for: ${formData.recipientName}, relationship to the person requesting the letter: ${formData.relationship}, purpose of letter: character reference letter, situation description: ${formData.situationDescription}, relationship nature and duration: ${formData.relationshipDuration}, character traits: ${formData.characterTraits}, achivements: ${formData.achievements}, personal growth: ${formData.personalGrowth}, contribution: ${formData.contribution}${formData.omittedInformation ? `, tone: ${formData.tone} and information to omit or avoid in letter is: ${formData.omittedInformation}` : ` and tone: ${formData.tone}`}. My name is ${formData.name} and my email is ${formData.email}. ${formData.additionalInformation ? ` additional information: ${formData.additionalInformation}` : ''}`;
    }
    else {
      request = `Write a draft of the letter for the following information. full name of the person the letter is for: ${formData.recipientName}, relationship to the person requesting the letter: ${formData.relationship}, purpose of letter: ${formData.letterPurpose}, desired job or industry: ${formData.desiredJob}, skills: ${formData.skills}${formData.accomplishments ? `, accomplishments: ${formData.accomplishments}` : ''}${formData.education ? `, educaton or training completed while incarcerated: ${formData.education}` : ''}, personal attributes: ${formData.personalAttributes}, willingness to learn: ${formData.willingnessToLearn}, employment explanation: ${formData.employmentExplanation}${formData.omittedInformation ? `, tone: ${formData.tone} and information to omitted or avoided in letter is: ${formData.omittedInformation}` : ` and tone: ${formData.tone}`}. My name is ${formData.name} and my email is ${formData.email}. Include 'To Whom It May Concern' at start of letter or To any specific company or Person who is mentioned in additonal information given after this in the start of letter and also include appropriate subject. ${formData.additionalInformation ? `additional information: ${formData.additionalInformation}. ` : `Include 'To Whom It May Concern' at start of letter.`}.`;
    }
    let response;
    try {
      response = await axios.post('https://api.openai.com/v1/completion', {
        prompt: request,
        max_tokens: 3000,
        model: 'text-davinci-003'
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer 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'
        }
      });
    } catch (error) {
      console.log('Error');
      return;
    }
    console.log('response', response);
    const body = response.data.choices[0].text;
  };
  return (
    <div>
      {isLoading ? <LoadingSpinner /> :
        isSuccessful ? <LoadingSpinner isSuccessful={true} /> :
          isFailed ? <LoadingSpinner isFailed={true} /> :
            <div>
              <h2 className='heading-1'>Generate a Letter</h2>
              <form className='form-1' onSubmit={handleSubmit}>
                <label className='label-1'>
                  Your Name:*
                  <input
                    className='common-1'
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </label>
                <label className='label-1'>
                  Your Email:*
                  <input
                    className='common-1'
                    type="text"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </label>
                <label className='label-1'>
                  Recipient Name:*
                  <input
                    className='common-1'
                    type="text"
                    name="recipientName"
                    value={formData.recipientName}
                    onChange={handleChange}
                    placeholder='full name of the person the letter is for'
                    required
                  />
                </label>
                <label className='label-1'>
                  Relationship:*
                  <input
                    className='common-1'
                    type="text"
                    name="relationship"
                    value={formData.relationship}
                    onChange={handleChange}
                    placeholder=' Relationship to the person requesting the letter(self, family member, friend, etc.)'
                    required
                  />
                </label>
                <label className='label-1-1'>
                  Purpose of the Letter:*
                  <select className='common-1-1' name="letterPurpose" value={formData.letterPurpose} onChange={handleChange}>
                    <option value="">Select Purpose</option>
                    <option value="character">Character Reference Letter</option>
                    <option value="employment">Employment Letter</option>
                  </select>
                </label>
                {formData.letterPurpose === 'character' ?
                  <div>
                    <label className='label-1'>
                      Situation Description:*
                      <input
                        className='common-1'
                        type="text"
                        name="situationDescription"
                        value={formData.situationDescription}
                        onChange={handleChange}
                        placeholder='brief description of the situation that requires the character reference (court hearing, parole board, etc.)'
                        required
                      />
                    </label>
                    <label className='label-1'>
                      Relationship Duration:*
                      <input
                        className='common-1'
                        type="text"
                        name="relationshipDuration"
                        value={formData.relationshipDuration}
                        onChange={handleChange}
                        placeholder='duration and nature of the relationship between you and recipient'
                        required
                      />
                    </label>
                    <label className='label-1'>
                      Character Traits:*
                      <input
                        className='common-1'
                        type="text"
                        name="characterTraits"
                        value={formData.characterTraits}
                        onChange={handleChange}
                        placeholder='positive character traits and examples of how these traits were demonstrated'
                        required
                      />
                    </label>
                    <label className='label-1'>
                      Achievements:*
                      <input
                        className='common-1'
                        type="text"
                        name="achievements"
                        value={formData.achievements}
                        onChange={handleChange}
                        placeholder='any achievements, community involvement, or volunteer work'
                        required
                      />
                    </label>
                    <label className='label-1'>
                      Personal Growth:*
                      <input
                        className='common-1'
                        type="text"
                        name="personalGrowth"
                        value={formData.personalGrowth}
                        onChange={handleChange}
                        placeholder='details of personal growth or rehabilitation efforts while incarcerated'
                        required
                      />
                    </label>
                    <label className='label-1'>
                      Contribution:*
                      <input
                        className='common-1'
                        type="text"
                        name="contribution"
                        value={formData.contribution}
                        onChange={handleChange}
                        placeholder='how the person will contribute positively to society upon release'
                        required
                      />
                    </label>
                  </div>
                  : formData.letterPurpose === 'employment' ?
                    <div>
                      <label className='label-1'>
                        Desired Job:*
                        <input
                          className='common-1'
                          type="text"
                          name="desiredJob"
                          value={formData.desiredJob}
                          onChange={handleChange}
                          placeholder='desired job or industry'
                          required
                        />
                      </label>
                      <label className='label-1'>
                        Skills:*
                        <input
                          className='common-1'
                          type="text"
                          name="skills"
                          value={formData.skills}
                          onChange={handleChange}
                          placeholder='relevant skills, qualifications, and experiences'
                          required
                        />
                      </label>
                      <label className='label-1'>
                        Accomplishments:
                        <input
                          className='common-1'
                          type="text"
                          name="accomplishments"
                          value={formData.accomplishments}
                          onChange={handleChange}
                          placeholder='examples of achievements or accomplishments in previous jobs or during incarceration (if applicable)'
                        />
                      </label>
                      <label className='label-1'>
                        Education:
                        <input
                          className='common-1'
                          type="text"
                          name="education"
                          value={formData.education}
                          onChange={handleChange}
                          placeholder='education or training completed while incarcerated (if applicable)'
                        />
                      </label>
                      <label className='label-1'>
                        Personal Attributes:*
                        <input
                          className='common-1'
                          type="text"
                          name="personalAttributes"
                          value={formData.personalAttributes}
                          onChange={handleChange}
                          placeholder='personal attributes that make the person a good fit for the desired job or industry'
                          required
                        />
                      </label>
                      <label className='label-1'>
                        Willingness to Learn:*
                        <input
                          className='common-1'
                          type="text"
                          name="willingnessToLearn"
                          value={formData.willingnessToLearn}
                          onChange={handleChange}
                          placeholder='willingness to learn and adapt to new environments'
                          required
                        />
                      </label>
                      <label className='label-1'>
                        Employment Explanation:*
                        <input
                          className='common-1'
                          type="text"
                          name="employmentExplanation"
                          value={formData.employmentExplanation}
                          onChange={handleChange}
                          placeholder="Explanation of how securing employment will support the person's successful reintegration into society"
                          required
                        />
                      </label>
                    </div>
                    : ''
                }
                <label className='label-1'>
                  Additional Information:
                  <input
                    className='common-1'
                    type="text"
                    name="additionalInformation"
                    value={formData.additionalInformation}
                    onChange={handleChange}
                    placeholder='any specific points the person would like to emphasize or include in the letter'
                  />
                </label>
                <label className='label-1-1'>
                  Tone:*
                  <select className='common-1-1' name="tone" value={formData.tone} onChange={handleChange}>
                    <option value="">Select Tone</option>
                    <option value="formal">Formal</option>
                    <option value="semi-formal">Semi-Formal</option>
                    <option value="casual">Casual</option>
                  </select>
                </label>
                <label className='label-1'>
                  Omitted Information:
                  <input
                    className='common-1'
                    type="text"
                    name="omittedInformation"
                    value={formData.omittedInformation}
                    onChange={handleChange}
                    placeholder='any information that should be omitted or avoided in the letter'
                  />
                </label>
                <button className='btn-1' type="submit">Generate Letter</button>
                <br />
              </form>
            </div>
      }
    </div>
  );
}

export default GenerateLetter;
