import React, { useEffect } from 'react';

function ContactUs() {
    useEffect(() => {
        window.scrollTo(0, 0);
      }, []);
      const [name, setName] = useState('');
      const [email, setEmail] = useState('');
      const [recipientName, setRecipientName] = useState('');
      const [recipientEmail, setRecipientEmail] = useState('');
      const [subject, setSubject] = useState('');
      const [body, setBody] = useState('');
    
      const handleSubmit = async (e) => {
        e.preventDefault();
        
        const emailRegex = /^\S+@\S+\.\S+$/;
        if (!emailRegex.test(email)) {
            alert('Please enter a valid sender email address');
            return;
        }
        else if(!emailRegex.test(recipientEmail)){
            alert('Please enter a valid recipient email address');
            return;
        }
        else if(email===recipientEmail)
        {
            alert('Sender and Recipient email address cannot be same');
            return;
        }
      };
      return (
        <div className='form-container'>
          <h2 className='heading-1'>Generate a Letter</h2>
          <form className='form-1' onSubmit={handleSubmit}>
            <label className='label-1'>
              Your Name:*
              <input
                className='common-1'
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </label>
            <label className='label-1'>
              Your Email:*
              <input
                className='common-1'
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </label>
            <label className='label-1'>
              Recipient's Name:*
              <input
                className='common-1'
                type="text"
                value={recipientName}
                onChange={(e) => setRecipientName(e.target.value)}
                required
              />
            </label>
            <label className='label-1'>
              Recipient's Email:*
              <input
                className='common-1'
                type="email"
                value={recipientEmail}
                onChange={(e) => setRecipientEmail(e.target.value)}
                required
              />
            </label>
            <label className='label-1'>
              Subject:*
              <input
                className='common-1'
                type="text"
                value={subject}
                onChange={(e) => setSubject(e.target.value)}
                required
              />
            </label>
            <label className='label-1'>
              Body:*
              <textarea
                className='common-1'
                value={body}
                onChange={(e) => setBody(e.target.value)}
                required
              />
            </label>
            <button className='btn-1' type="submit">Generate Letter</button>
            <br/>
          </form>
        </div>
      );
}

export default ContactUs;
