import React, { useEffect } from 'react';
import {useState} from 'react';
function ContactUs() {
    useEffect(() => {
        window.scrollTo(0, 0);
      }, []);
      const [name, setName] = useState('');
      const [email, setEmail] = useState('');
      const [body, setBody] = useState('');
    
      const handleSubmit = async (e) => {
        e.preventDefault();
        
        const emailRegex = /^\S+@\S+\.\S+$/;
        if (!emailRegex.test(email)) {
            alert('Please enter a valid sender email address');
            return;
        }
        else if(!emailRegex.test(recipientEmail)){
            alert('Please enter a valid recipient email address');
            return;
        }
        else if(email===recipientEmail)
        {
            alert('Sender and Recipient email address cannot be same');
            return;
        }
      };
      return (
        <div className='form-container'>
          <h2 className='heading-1'>Contact Us</h2>
          <form className='form-1' onSubmit={handleSubmit}>
            <label className='label-1'>
              Your Name:*
              <input
                className='common-1'
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </label>
            <label className='label-1'>
              Your Email:*
              <input
                className='common-1'
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </label>
            <label className='label-1'>
              Message:*
              <textarea
                className='common-1'
                value={body}
                onChange={(e) => setBody(e.target.value)}
                required
              />
            </label>
            <button className='btn-1' type="submit">Submit</button>
            <br/>
          </form>
        </div>
      );
}

export default ContactUs;
