import React from "react";
import "../Styles/Loader.css";

function LoadingSpinner({ isSuccessful, isFailed }) {
    if (isSuccessful) {
        return (
            <div className="success-container">
                <div className="success-icon">&#10004;</div>
                <div className="success-para">Form submitted successfully!</div>
            </div>
        );
    } else if (isFailed) {
        return (

            <div className="failure-container">
                <div className="failure-icon">&#10060;</div>
                <div className="failure-para">Sorry! We are unable to record your response. Please try again.</div>
            </div>
        );
    } else {
        return (
            <div className="loader-cotainer">
                <div className="spinner-container">
                    <div className="loading-spinner"></div>
                    <div className="Loading-para">
                        Please wait while we are gathering data....
                    </div>
                </div>
            </div>
        );
    }
}

export default LoadingSpinner;
