import React from 'react';
import { useState } from 'react';
import "../Styles/GenerateLetter.css";
function Generate_Letter() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [recipientName, setRecipientName] = useState('');
  const [recipientEmail, setRecipientEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [body, setBody] = useState('');



  const handleSubmit = async (e) => {
    e.preventDefault();
    // Handle form submission here
    // const emailRegex = /^\S+@\S+\.\S+$/;
    // if (!emailRegex.test(email)) {
    //     alert('Please enter a valid email address');
    // return;
    // }
        const response = await axios.post('https://api.openai.com/v1/completions', {
          prompt: `write a letter draft for the following information. Body: ${body}, Subject: ${subject}, recievers name: ${recipientName}, senders name: ${name}`,
          max_tokens: 1000,
          model: 'text-davinci-003'
        }, {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer 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'
          }
        });
        return response.data.choices[0].text;
  };
  return (
    <div>
      <h2 className='heading-1'>Generate a Letter</h2>
      <form className='form-1' onSubmit={handleSubmit}>
        <label className='label-1'>
          Your Name:*
          <input
            className='common-1'
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </label>
        <label className='label-1'>
          Your Email:*
          <input
            className='common-1'
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </label>
        <label className='label-1'>
          Recipient's Name:*
          <input
            className='common-1'
            type="text"
            value={recipientName}
            onChange={(e) => setRecipientName(e.target.value)}
            required
          />
        </label>
        <label className='label-1'>
          Recipient's Email:*
          <input
            className='common-1'
            type="email"
            value={recipientEmail}
            onChange={(e) => setRecipientEmail(e.target.value)}
            required
          />
        </label>
        <label className='label-1'>
          Subject:*
          <input
            className='common-1'
            type="text"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            required
          />
        </label>
        <label className='label-1'>
          Body:*
          <textarea
            className='common-1'
            value={body}
            onChange={(e) => setBody(e.target.value)}
            required
          />
        </label>
        <button className='btn-1' type="submit">Generate Letter</button>
        <br/>
      </form>
    </div>
  );
}

export default Generate_Letter;
