import React, { useEffect } from 'react';
import {useState} from 'react';
import "../Styles/ContactUs.css";
import nodemailer from 'nodemailer';
function ContactUs() {
    useEffect(() => {
        window.scrollTo(0, 0);
      }, []);
      const [name, setName] = useState('');
      const [email, setEmail] = useState('');
      const [body, setBody] = useState('');
      const sendEmail = async (name, email, message) => {
  try {
    const transporter = nodemailer.createTransport({
      service: 'gmail',
      auth: {
        user: 'your-gmail-account@gmail.com', // your Gmail account email
        pass: 'your-gmail-account-password', // your Gmail account password
      },
    });

    const mailOptions = {
      from: 'your-gmail-account@gmail.com', // sender address
      to: 'recipient-email@example.com', // list of receivers
      subject: 'New message from your website', // Subject line
      html: `
        <p><strong>Name:</strong> ${name}</p>
        <p><strong>Email:</strong> ${email}</p>
        <p><strong>Message:</strong> ${message}</p>
      `,
    };

    await transporter.sendMail(mailOptions);

    console.log('Email sent successfully');
  } catch (error) {
      console.error('Error while sending email', error);
  }
};

      function ClearForm()
      {
        setName('');
        setEmail('');
        setBody('');
      };

      const handleSubmit = async (e) => {
        e.preventDefault();
        const emailRegex = /^\S+@\S+\.\S+$/;
        if (!emailRegex.test(email)) {
            alert('Please enter a valid email address');
            return;
        }
        else{
          
          alert('Form Submitted Successfully!');
          ClearForm();
        }
        console.log(name,email,body);
        await sendEmail(name, email, body);
      };
      return (
        <div className='form-container'>
          <h2 className='heading-2'>Contact Us</h2>
          <form className='form-2' onSubmit={handleSubmit}>
            <label className='label-2'>
              Your Name:*
              <input
                className='common-2'
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </label>
            <label className='label-2'>
              Your Email:*
              <input
                className='common-2'
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </label>
            <label className='label-2'>
              Message:*
              <textarea
                className='common-2'
                value={body}
                onChange={(e) => setBody(e.target.value)}
                required
              />
            </label>
            <button className='btn-2' type="submit">Submit</button>
            <br/>
          </form>
        </div>
      );
}

export default ContactUs;
